import React from 'react'
import { Link } from 'react-router-dom'

export default function ListItem({ note }) {

    let getTitle = (note) => {
        const title = node.body.split('\n')[0]
        return title
    }

    return (

        <Link to={`/note/${note.id}`}>
            <div className="notes-list-item">
                <h3>{getTitle(note)}</h3>
            </div>
        </Link>
    )
}
